llpml:
struct Point
{
  var x, y;

  function Show()
  {
    printfln("[%s] %d, %d", __FUNCTION__, x, y);
  }
}

struct DoublePoint
{
  Point a, b;

  function Show()
  {
    printfln("[%s] %p -> a: %p", __FUNCTION__, this, a);
    a.Show();
    printfln("[%s] %p -> b: %p", __FUNCTION__, this, b);
    b.Show();
  }
}

DoublePoint p = { { 1, 2 }, { 3, 4 } };

printfln("p = %p", p);
printfln("addrof(p.a) = %p", addrof(p.a));
printfln("addrof(p.b) = %p", addrof(p.b));
printfln("addrof(p.b.y) = %p", addrof(p.b.y));

vprintfln("p = ((%d, %d), (%d, %d))", p);
p.Show();
p.a.Show();
p.b.Show();
